$(document).ready(function(){$(".gallery").mauGallery({columns:{xs:1,sm:2,md:3,lg:3,xl:3},lightBox:true,lightboxId:"myAwesomeLightbox",showTags:true,tagsPosition:"top"});});$(document).ready(function(){try{$(".nav-pills .nav-link").on("click",function(){console.log("Un lien a été cliqué.");$(".nav-pills .nav-link").removeClass("active");console.log('Toutes les classes "active" ont été supprimées.');$(this).addClass("active");console.log('Classe "active" ajoutée à l\'élément cliqué.');});}catch(error){console.error("Une erreur est survenue :",error);}});$(document).ready(function(){let currentImageIndex=0;function getVisibleImages(){return $(".gallery-item:visible");}function showImage(index){const visibleImages=getVisibleImages();const newImageSrc=$(visibleImages[index]).attr("src");$(".lightboxImage").attr("src",newImageSrc);}$(".gallery-item").on("click",function(){const visibleImages=getVisibleImages();currentImageIndex=visibleImages.index(this);showImage(currentImageIndex);});$(".mg-prev").on("click",function(){const visibleImages=getVisibleImages();currentImageIndex=currentImageIndex>0?currentImageIndex-1:visibleImages.length-1;showImage(currentImageIndex);});$(".mg-next").on("click",function(){const visibleImages=getVisibleImages();currentImageIndex=currentImageIndex<visibleImages.length-1?currentImageIndex+1:0;showImage(currentImageIndex);});});
